static void Main(String[] args){

    switch ('A')
    {
        case 'A':
            System.out.println( "it's A" );
            break;
        case 'B':
            System.out.println( "it's B" );
            break;
        default:
            System.out.println( "It's neither A nor B" );
            break;
    }
}

/* Static because we only want one instance of this Method (no objects)*/
static void func(String name){
    System.out.println( "your name is " + name );
}

/* a and b are passed by value objects would be passed by reference */
static int add(int a, int b){
    return a + b; //gives this back to the Method caller
}


static void Main(String[] args){
    func("tom"); //prints your name is tom

    add(4,5); // returns 9

    func(add(5,6)); // prints your name is 11
}
